#If conditions are met we buy the token
def buy(buythistoken):
    
    spend = web3.toChecksumAddress("0xbb4CdB9CBd36B01bD1cBaEBF2De08d9173bc095c")  #wbnb contract address

    nonce = web3.eth.get_transaction_count(sender_address)

    pancakeswap2_txn = contractbuy.functions.swapExactETHForTokens(
    0, # set to 0, or specify minimum amount of token you want to receive - consider decimals!!!
    [spend,buythistoken],
    sender_address,
    (int(time.time()) + 10000)
    ).buildTransaction({
    'from': sender_address,
    'value': web3.toWei(0.0001,'ether'),#This is the Token(BNB) amount you want to Swap from
    'gasPrice': web3.toWei('5','gwei'),
    'nonce': nonce,
    })

    signed_txn = web3.eth.account.sign_transaction(pancakeswap2_txn, private_key=config.private)
    tx_token = web3.eth.send_raw_transaction(signed_txn.rawTransaction)
    print("Snipe was succesfull, bought: " + web3.toHex(tx_token))


